import TodoList from "./components/TodoList";
import Textfield from '@atlaskit/textfield';
import Button from '@atlaskit/button';
import { useState } from "react";

function App() {
  const [todoList, setTodoList] = useState([]);
  const [textInput, setTextInput] = useState("");

  const onTextInputChange = (e) => {
    setTextInput(e.target.value);
  };

  const onAddBtnClick = (e) => {
    //them text input vao danh sach todoList
    setTodoList([...todoList, {id: '', name: textInput, isCompleted: false }]);
  };

  return (
    <>
      <h3>Danh sach can lam</h3>
      <Textfield 
      name="app-todo" 
      placeholder='Them viec can lam...' 
      elemAfterInput={
        <Button isDisabled={true} appearance='primary' onClick={onAddBtnClick}>
          Them
        </Button>
      }
      css={{padding:"2px 4px 2px"}}
      value={textInput}
      onChange= {onTextInputChange}
      ></Textfield>
      <TodoList/>
    </>
  );
}

export default App;
